import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import classes from "./AddUsers.module.css";

const AddUsers = (props) => {
  const [isUsername, setIsUsername] = useState("");
  const [isAge, setIsAge] = useState("");
  const [isError, setIsError] = useState("");

  const submitHandler = (event) => {
    event.preventDefault();

    if (isUsername.trim().length === 0 || isAge.trim().length === 0) {
      setIsError({
        title: "Invalid Input",
        content: "Please Enter A Valid Username/Password.",
      });
      return;
    }
    if (+isAge < 1) {
      setIsError({
        title: "Invalid Input",
        content: "Age should be greater than 1.",
      });
      return;
    }
    props.onAddUsers(isUsername, isAge);
    setIsUsername("");
    setIsAge("");
  };

  const usernameHandler = (event) => {
    setIsUsername(event.target.value);
  };

  const ageHandler = (event) => {
    setIsAge(event.target.value);
  };

  const errorHandler = () => {
    setIsError(null);
  };

  return (
    <div>
      {isError && (
        <ErrorModal
          title={isError.title}
          content={isError.content}
          onConfirm={errorHandler}
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={submitHandler}>
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            placeholder="Enter username"
            value={isUsername}
            onChange={usernameHandler}
          ></input>
          <label htmlFor="age">Age (In years)</label>
          <input
            id="age"
            type="number"
            placeholder="Enter age"
            value={isAge}
            onChange={ageHandler}
            onConfirm={errorHandler}
          ></input>
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </div>
  );
};

export default AddUsers;
